import React from "react";
import  ReactDOM  from "react-dom";
import SingleComment from './SingleComment';
import UserCard from "./UserCard";
const App = () => {
    return(
    <div className="ui comments">
        <UserCard>
        <SingleComment name ='Alex' date ='Today at 5:00 PM' text ='it is amazing'/>
        </UserCard>
        <UserCard>
        <SingleComment name ='Jack' date ='Today at 6:00 PM' text ='Great Job'/>
        </UserCard>
        <UserCard>
        <SingleComment name ='Sarah' date ='Today at 7:00 PM' text ='Thanks'/>
        </UserCard>
    </div>
    
    )
}
ReactDOM.render(
    <App/>,
    document.querySelector('#root')
)
